INSERT INTO `tbl_comments` (`comments_id`, `blog_id`, `comments_author_name`, `comments_author_email`, `comments`, `publication_status`, `created_date_time`) VALUES
(1, 9, 'Jannatul Miskat', '0', 'আইসিটি বিভাগ আয়োজিত এ সংবাদ সম্মেলনে আগামী চার বছরের কর্মপরিকল্পনাকে ‘আইসিটি মাস্টারপ্ল্যান’ হিসেবে অভিহিত করা হয়েছে। এ পরিকল্পনায় রয়েছে চার বছরের মধ্যে একটি জাতীয়ভিত্তিক কলসেন্টার স্থাপন, বিশ্বের পঞ্চম ', 0, '2015-01-13 10:00:37'),
(2, 8, 'Jannatul Miskat', 'miskat@yahoo.com', 'অস্ট্রেলিয়ার উইকেট মানেই চোখধাঁধানো গতি। মুখের দিকে ধেয়ে আসা সব বাউন্সার। পেসাররাই সেখানে ব্যাটসম্যানদের যমদূত। সাপের মতো ছোবল দেওয়া দূরের কথা, স্পিনারদের ঘূর্ণিবলই বরং ওইসব উইকেটে কিছুটা স্বস্তি দেয় ব্যাটসম্যানদের। বিশ্বকাপের উইকেট শেষ পর্যন্ত কেমন হবে কে জানে, তবে অস্ট্রেলিয়ার উইকেট সম্পর্কে সাধারণ ধারণা এমনই।', 1, '2015-01-13 10:02:26'),
(3, 8, 'Jannatul Miskat', 'miskat@yahoo.com', 'অস্ট্রেলিয়ার উইকেট মানেই চোখধাঁধানো গতি। মুখের দিকে ধেয়ে আসা সব বাউন্সার। পেসাররাই সেখানে ব্যাটসম্যানদের যমদূত। সাপের মতো ছোবল দেওয়া দূরের কথা, স্পিনারদের ঘূর্ণিবলই বরং ওইসব উইকেটে কিছুটা স্বস্তি দেয় ব্যাটসম্যানদের। বিশ্বকাপের উইকেট শেষ পর্যন্ত কেমন হবে কে জানে, তবে অস্ট্রেলিয়ার উইকেট সম্পর্কে সাধারণ ধারণা এমনই।', 1, '2015-01-13 10:17:56'),
(4, 8, 'Jannatul Miskat', 'miskat@yahoo.com', 'অস্ট্রেলিয়ার উইকেট সম্পর্কে ধারণা পেতে টেলিভিশনে নিয়মিত দেখেছেন সদ্য শেষ হওয়া ভারত-অস্ট্রেলিয়া সিরিজের ম্যাচ, বাদ দিচ্ছেন না বিগ ব্যাশও। তা ছাড়া অস্ট্রেলিয়ায় যাওয়ার পরও হাতে বেশ কিছুদিন সময় পাওয়া যাবে। বাউন্সি উইকেটের সঙ্গে মানিয়ে নেওয়াটা তাই সমস্যা মনে হচ্ছে না সানির। আর অস্ট্রেলিয়ার উইকেট সম্পর্কে যাঁদের ধারণা আছে, তাঁদের সাহায্য তো পাচ্ছেনই। বিগ ব্যাশে খেলার সুবাদে সাকিব এ ব্যাপারে অনেকটাই অভিজ্ঞ এখন। ', 1, '2015-01-13 10:18:11'),
(5, 8, 'Jannatul Miskat', 'miskat@yahoo.com', 'গ্রুপপর্বে বাংলাদেশের দুটি ম্যাচ আছে নিউজিল্যান্ডেও। তবে অস্ট্রেলিয়াতেই চারটি ম্যাচ বলে বাংলাদেশে বিশ্বকাপ নিয়ে আলোচনায় ‘অস্ট্রেলিয়া, অস্ট্রেলিয়া’ রবই বেশি শোনা যাচ্ছে। যে অস্ট্রেলিয়াকে মনে করা হয় পেসারদের স্বর্গভূমি। সেখানে ব্যাটসম্যানদের কাজটা তো চ্যালেঞ্জিং হবেই, স্পিনারদের কাজটাও কি নয়?', 1, '2015-01-13 10:20:44'),
(6, 8, 'Saahil Alam Talha', 'saahil.alam@yahoo.com', 'অস্ট্রেলিয়ার উইকেট সম্পর্কে ধারণা পেতে টেলিভিশনে নিয়মিত দেখেছেন সদ্য শেষ হওয়া ভারত-অস্ট্রেলিয়া সিরিজের ম্যাচ, বাদ দিচ্ছেন না বিগ ব্যাশও। তা ছাড়া অস্ট্রেলিয়ায় যাওয়ার পরও হাতে বেশ কিছুদিন সময় পাওয়া যাবে। বাউন্সি উইকেটের সঙ্গে মানিয়ে নেওয়াটা তাই সমস্যা মনে হচ্ছে না সানির। আর অস্ট্রেলিয়ার উইকেট সম্পর্কে যাঁদের ধারণা ', 1, '2015-01-13 10:30:19');

